<script>
import { snapPoints, controlPoints } from "../../utils/SnapPoints.js";
import { getFillColor } from "../../utils/index.js";
import Vue from 'vue';

export default {
  data() {
    return {
      snapPoints,
      controlPoints,
      hovered: [],
      hoveredPannos: [],
      selected: [],
    };
  },
  mounted() {
    const snapPoints = document.getElementsByClassName("snap-point");
    const groupSnapPoints = document.getElementsByClassName("group-snap-point");
    this.initSanpAction(snapPoints);
    this.intGroupSnapAction(groupSnapPoints);
  },
  methods: {
    intGroupSnapAction(elements) {
      for (const element of elements) {
        element.addEventListener(
          "mouseover",
          () => {
            if (this.$store.state.roundStatus != 'started')
              return;
            // betting hover
            // const _ids = this.$store.state.selected.map((m) => m.refer);
            const _hovers = [];
            // for (const id of _ids) {
            //   _hovers.push(document.getElementById(id).getAttribute("hover"));
            // }
            if (element.getAttribute("hover") != null) {
              _hovers.push(element.getAttribute("hover"));
              this.setHovered(_hovers.join(" "), false);
            }
          },
          false
        );
        element.addEventListener(
          "mouseout",
          () => {
            if (this.$store.state.roundStatus != 'started')
              return;
            // betting hover
            // const _ids = this.$store.state.selected.map((m) => m.refer);
            // const _hovers = [];
            // for (const id of _ids) {
            //   _hovers.push(document.getElementById(id).getAttribute("hover"));
            // }

            // this.setHovered(_hovers.join(" "), false);
            this.$store.commit("setHovered", []);
          },
          false
        );
        element.addEventListener(
          "click",
          (e) => {
            this.handleClicked(e);
          },
          false
        );
      }
    },
    initSanpAction(elements) {
      for (const element of elements) {
        // element.removeEventListener("mouseover", () => {}, false);
        element.addEventListener(
          "mouseover",
          () => {
            if (this.$store.state.roundStatus != "started") return;
            // betting hover
            // const _ids = this.$store.state.selected.map((m) => m.refer);
            const _hovers = [];
            // for (const id of _ids) {
            //   _hovers.push(document.getElementById(id).getAttribute("hover"));
            // }
            if (element.getAttribute("hover") != null) {
              _hovers.push(element.getAttribute("hover"));
              this.setHovered(_hovers.join(" "), false);
            }
          },
          false
        );
        // element.removeEventListener("mouseout", () => {}, false);
        element.addEventListener(
          "mouseout",
          () => {
            // betting hover
            if (this.$store.state.roundStatus != "started") return;
            const _ids = this.$store.state.selected.map((m) => m.refer);
            const _hovers = [];
            for (const id of _ids) {
              _hovers.push(document.getElementById(id).getAttribute("hover"));
            }

            // this.setHovered(_hovers.join(" "), false);
            this.$store.commit("setHovered", []);
          },
          false
        );
        // element.removeEventListener("click", () => {}, false);
        element.addEventListener(
          "click",
          (e) => {
            this.handleClicked(e);
          },
          false
        );
      }
    },
    checkGroupBet(point) {
      if (this.$store.state.showGroupBet) {
        return true;
      } else {
        return controlPoints.filter((c) => c.refer == point.refer).length == 0;
      }
    },
    getFillColor(value, max) {
      return getFillColor(value, max);
    },
    handleClicked(e) {
      if (this.$store.state.roundStatus != "started") {
        return;
      }

      const arr = this.$store.state.selected;
      const userBalance = this.$store.state.haveBalance;
      if ((eval(userBalance) - eval(this.$store.state.coin)) < 0 && (this.$store.state.betAction === 'add')) {
        let instance = Vue.$toast.error(`Limit balance, please check your balance!`);
        return;
      }
      this.$store.commit("setUpdated",
        [{
          refer: e.target.id,
          value: this.$store.state.coin,
        }]);
      if (arr.filter((v) => v.refer == e.target.id).length == 0) {
        this.$store.commit('setBetAction', 'add');
        arr.push({
          refer: e.target.id,
          value: this.$store.state.coin,
        });
        this.selected = arr;


        this.$store.commit("setSelected", arr);
        setTimeout(() => {
          this.$store.commit("setHovered", []);
        }, 500);
      } else {
        // remove chip
        if (this.$store.state.betAction == "remove") {
          const _arr = arr.filter((v) => v.refer != e.target.id);
          this.selected = _arr;
          this.$store.commit("setSelected", _arr);
          setTimeout(() => {
            this.$store.commit("setHovered", []);
          }, 500);
        }
        if (this.$store.state.betAction == "add") {
          const _arr = arr.filter((v) => {
            if (v.refer == e.target.id) {
              v.value += this.$store.state.coin;
            }
            return v;
          });
          this.$store.commit("setSelected", _arr);
          setTimeout(() => {
            this.$store.commit("setHovered", []);
          }, 500);
        }
      }
    },

    setHovered(_hovered, isOvale) {
      const _hovers = _hovered.trim().includes(" ")
        ? _hovered.split(" ")
        : [_hovered.trim()];
      const _hoverPannos = [];
      const _coinPannos = [];

      for (const id of _hovers) {
        if (isOvale) {
          const hover = document.getElementById(id).getAttribute("hover");
          if (hover && hover != null) {
            const hovers = hover.includes(" ") ? hover.split(" ") : [hover.trim()];

            for (const pannoId of hovers) {
              _hoverPannos.push({ refer: `g${pannoId}` });
              _coinPannos.push({ refer: pannoId });
            }
          }
        } else {
          _hoverPannos.push({ refer: `g${id}` });
          _coinPannos.push({ refer: id });
        }
      }
      this.$store.commit("setHovered", _hoverPannos);
    },
  },
  computed: {
    filterCoins() {
      const selectedCoins = this.$store.state.selected;
      const coins = [];


      for (const point of this.controlPoints) {
        for (const coin of selectedCoins) {
          if (coin.refer == point.refer) {
            coins.push({
              ...point,
              value: coin.value,
            });
          }
        }
        if (this.$store.state.winCoin !== null && point.refer === this.$store.state.winCoin.refer) {
          coins.push({
            ...point,
            value: this.$store.state.winCoin.value,
          });
        }
      }

      for (const point of this.snapPoints) {
        for (const coin of selectedCoins) {
          if (coin.refer == point.refer) {
            coins.push({
              ...point,
              value: coin.value,
            });
          }
        }
        if (this.$store.state.winCoin !== null && point.refer === this.$store.state.winCoin.refer) {
          coins.push({
            ...point,
            value: this.$store.state.winCoin.value,
          });
        }
      }

      return coins;
    },
  },
};
</script>

